package practicas;

public class Ejercicio2 {

	//muestra las tablas del 1 al 10
	
	public static void main(String[] args) {
		// TODO Auto-generated method stub

				
		for (int i = 1; i < 11; i++ ) {
			System.out.println("TABLA DEL  " + i);
			for (int j = 1; j < 11; j++) {
		
		System.out.println(i + "x" + j + " = " + (i*j));
			}
		}
	}
		
		
}


